// 根據 action 類型構建不同的 prompt
const text = $json.body.text;
const action = $json.body.action || 'summarize';
const language = $json.body.target_language || 'Traditional Chinese';

let prompt = '';

switch(action) {
  case 'translate':
    prompt = `請將以下文本翻譯成${language}，只回覆翻譯結果，不要有任何其他說明：\n\n${text}`;
    break;
  case 'reply':
    prompt = `請基於以下內容生成專業且有幫助的回覆：\n\n${text}`;
    break;
  case 'note':
    prompt = `請將以下文本整理成結構化的筆記，包含主要要點、次要細節和關鍵結論:\n\n${text}`;
    break;
  case 'summarize':
  default:
    prompt = `請用中文將以下文本摘要為 3-5 個核心要點，每個要點用 • 開頭，簡潔清楚：\n\n${text}`;
    break;
}

return [{
  prompt: prompt,
  action: action,
  text: text,
  language: language
}];

// 解析 Gemini API 回應
try {
  const response = $json;
  const apiResponse = response.body || response;

  // 檢查是否有錯誤
  if (apiResponse.error) {
    return [{
      success: false,
      result: 'API Error',
      error: apiResponse.error.message,
      action: $node["Build Prompt"].json.action
    }];
  }

  // 提取文本結果
  if (apiResponse.candidates && apiResponse.candidates.length > 0) {
    const candidate = apiResponse.candidates[0];
    if (candidate.content && candidate.content.parts && candidate.content.parts.length > 0) {
      const result = candidate.content.parts[0].text;
      return [{
        success: true,
        result: result,
        action: $node["Build Prompt"].json.action,
        timestamp: new Date().toISOString()
      }];
    }
  }

  return [{
    success: false,
    result: 'No valid response',
    error: 'Invalid API response structure',
    action: $node["Build Prompt"].json.action
  }];
} catch (error) {
  return [{
    success: false,
    result: 'Error processing response',
    error: error.message,
    action: $node["Build Prompt"].json.action
  }];
}
